package Classwork;

public class SingleArrayCW {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String Rainbow [] = new String[7];

		Rainbow[0]="red";
		Rainbow[1]="yellow";
		Rainbow[2]="blue";
		Rainbow[3]="green";
		Rainbow[4]="violet";
		Rainbow[5]="indigo";
		Rainbow[6]="orange";

		for (int x=0; x<Rainbow.length; x++)
		{System.out.println(Rainbow[x]);}

	}

}
