package Homework;

public class SingleArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String Months [] = new String[12];

		Months[0] = "January";
		Months[1] = "February";
		Months[2] = "March";
		Months[3] = "April";
		Months[4] = "May";
		Months[5] = "June";
		Months[6] = "July";
		Months[7] = "August";
		Months[8] = "September";
		Months[9] = "October";
		Months[10] = "November";
		Months[11] = "December";

		for (int i=0; i<3; i++)
		{System.out.println(Months[i]);}

		for (int i=9; i<12; i++)
		{System.out.println(Months[i]);}

	}

}
